--- 货币兑换
---@param packet any
function handles:S2CCoinExchange(packet)
    local player = self
    local id = packet.id
    local stateCode = packet.stateCode
    if stateCode == 0 then
        Lib.emitEvent(Event.EVENT_GAME_COIN_EXCHANGE, true, id)
        Me:showGameTopTips(Lang:toText("g2068.gui.coin.exchange.success"))
    else
        Lib.emitEvent(Event.EVENT_GAME_COIN_EXCHANGE, false, id)
        Me:showGameTopTips(Lang:toText("g2068.gui.coin.exchange.fail"))
    end
end
