---初始化数据库的数据
function Entity:setDBData(data)
    self:setData(Define.VarKey.Skills, data.skills or {})
    self:setData(Define.VarKey.EquippedSkills, data.equippedSkills or {})
end

---怪物回归到出生地
function EntityServer:monsterBackBornPos()
    if not self or not self:isValid() then
        return
    end
    self:stopAI()
    self:setMapPos(self.map, self:data("main").regionBirthPos)
    self:exitStateType(Define.RoleStatus.BATTLE_STATE)
    self:startAI()
end

function EntityServer:setPlayerIsInBattleState(value)
    local data=self:getActionMappingData()
    data.isInBattleState=value
    self:setActionMappingData(data)
end

function EntityServer:setPlayerActionMap(normalMap,battleMap)
    local data=self:getActionMappingData()
    data.actionMapNormal=normalMap
    data.actionMapBattle=battleMap
    self:setActionMappingData(data)
end