local handles = T(Player, "PackageHandlers")

--- 传送成功
---@param packet any
function handles:S2CEnterMission(packet)
    ---@type Entity
    local player = self
    local stateCode = packet.stateCode
    if stateCode ~= 0 then
        --- 弹出失败
        player:showGameTopTips(Lang:toText("g2069_mission_in_fail"))
    end
    Lib.emitEvent(Event.EVENT_GAME_MISSION_ENTER_MISSION, stateCode == 0)
end


function handles:S2CMissionTeammateCanSelect(packet)
    Lib.emitEvent(Event.EVENT_GAME_MISSION_SELECT_TEAMMATE, packet.missionId, packet.playerList)
end