local Player = Player

-- 请求可一起进入副本的玩家列表
function Player:requestMissionTeammateCanSelect(missionId)
    Me:sendPacket({
        pid = "C2SMissionTeammateCanSelect",
        missionId = missionId
    })
end

-- 请求开启副本
function Player:requestOpenMissionTeleport(missionMode, missionId, selectUserList)
    Me:sendPacket({
        pid = "C2SMissionOpenMissionTeleport",
        missionMode = missionMode,
        missionId = missionId,
        selectUserList = selectUserList
    })
end
