GMItem["GMmon/Print Hazard Index"] = function(self)
    if not World.cfg.openGM then
        return
    end
    local dangerValue = Me:getDangerValue()
    local content = "危险指数：" .. dangerValue
    Plugins.CallTargetPluginFunc("fly_new_tips", "pushFlyNewTipsText", content)
end

GMItem["GMmon/on Boot effects"] = function(self)
    if not World.cfg.openGM then
        return
    end
    Me:showTaskGuideArrow(true)
end
GMItem["GMmon/off boot effects"] = function(self)
    if not World.cfg.openGM then
        return
    end
    Me:showTaskGuideArrow(false)
end

