---@class GameSkillSoundConfig
local GameSkillSoundConfig = T(Config, "GameSkillSoundConfig")

local GameSkillSoundMap = {}

function GameSkillSoundConfig:init()
    local config = Lib.read_csv_file(Root.Instance():getGamePath() .. "config/game_skill_sound.csv", 2)
    for _, vConfig in pairs(config or {}) do
        if vConfig.soundName ~= "" then
            local data = {}
            data.id = tonumber(vConfig.id)
            data.soundName = vConfig.soundName
            data.sound = vConfig.soundPath
            data.volume = tonumber(vConfig.volume) or 1
            data.loop = false
            data.selfOnly = false
            GameSkillSoundMap[vConfig.soundName] = data
        end
    end
end

function GameSkillSoundConfig:getSkillSoundConfig(soundName)
    return GameSkillSoundMap[tostring(soundName)]
end

function GameSkillSoundConfig:getAllSkillSoundConfig()
    local soundList = {}
    for _, sound in pairs(GameSkillSoundMap) do
        table.insert(soundList, sound)
    end
    table.sort(soundList, function(sound1, sound2)
        return sound1.id < sound2.id
    end)
    return soundList
end

GameSkillSoundConfig:init()

return GameSkillSoundConfig