-- 根据游戏id获取同玩好友和非同玩好友userId列表
function AsyncProcess.GetChatFriendUserIdWithGameId(userId, type, callback)
    local url = strfmt("%s/gameaide/api/%s/inner/game/friends/ids/%s", self.ServerHttpHost, "v1", userId)
    local params = {
        { "gameId", World.GameName },
        { "type", type }
    }
    self.HttpRequest("GET", url, params, function (response, isSuccess)
        if not isSuccess then
            print("GetChatFriendUserIdWithGameId Error: " , response.code)
            return
        end
        callback(response.data)
    end, {}, true)
end

