local function safeDecodeJSON(content,def)
    if content == "" then
        return def or false
    end
    local ok, ret = xpcall(cjson.decode, debug.traceback, content)
    if not ok then
        print("json decode fail:",content)
        return def or false
    end
    return ret
end
