local handles = T(Player, "PackageHandlers")
function handles:S2CPlayPlayerAction(packet)
    local entity=World.CurWorld:getEntity(packet.objID)
    if entity and entity:isValid() then
        entity:updateUpperAction(packet.action, -1)
        entity:setPlayerActionInf(packet.action)
    end
end

function handles:S2CStopPlayerAction(packet)
    local entity=World.CurWorld:getEntity(packet.objID)
    if entity and entity:isValid()  then
        local actionInf=self:getPlayerActionInf()
        --print(">>>>>>>>>>>>S2CStopPlayerAction:",actionInf.actionName)
        if actionInf.actionName then
            entity:updateUpperAction(actionInf.actionName, 1)
            entity:setPlayerActionInf(nil)
        end
    end
end

